package Test1;

public class test3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		int num = 1;
		
		result = num++;
		System.out.println("result:" + result);
		
		result = ++num;
		System.out.println("result : " + result);
		
		result = num --;
		System.out.println("result : " + result);
		
		result = --num;
		System.out.println("result : " + result);
		
	}

}
